import React from "react";

const CharacterCard = ({ character, isActive, onToggle }) => {
  return (
    <div className={`card relative ${character.waveColor} p-4 m-4 rounded-lg ${isActive ? "Activecard" : ""}`}>
      <svg className="wave" viewBox="0 0 1440 320" preserveAspectRatio="none">
        <path fill="#ffffff" fillOpacity="0.5" d="M0,160 C480,260 960,60 1440,160 L1440,0 L0,0 Z" />
      </svg>

      <img
        src={character.image}
        alt={character.name}
        className="card_image absolute transition-all duration-500 ease-in-out"
      />

      <div className="card-body flex flex-col gap-2 mt-32">
        <h2 className="text-xl font-bold">{character.name}</h2>
        <p className="italic text-sm">"{character.quote}"</p>
        <button
          onClick={() => onToggle(character)}
          className={`playbtn text-white ${
            isActive ? "bg-red-700" : "bg-blue-700"
          } hover:bg-opacity-80 font-medium rounded-lg text-sm px-5 py-2.5`}
        >
          {isActive ? "Hide" : "Play"}
        </button>
      </div>
    </div>
  );
};

export default CharacterCard;
