import React, { useState } from "react";
import CharacterCard from "./";
import characters from "../data/characterData";
import change_image_character from "../scripts/changeCharaterImage";

const AnimefyLayout = () => {
  const [activeCharId, setActiveCharId] = useState(null);

  const handleToggle = (char) => {
    const isActive = activeCharId === char.id;
    setActiveCharId(isActive ? null : char.id);
    change_image_character(char.id, isActive ? 2 : 1, char.waveColor);
  };

  const activeCharacter = characters.find((c) => c.id === activeCharId);

  return (
    <div className="container mx-auto p-4">
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {characters.map((char) => (
          <CharacterCard
            key={char.id}
            character={char}
            isActive={activeCharId === char.id}
            onToggle={handleToggle}
          />
        ))}
      </div>

      {/* Video player overlay */}
      {activeCharacter && (
        <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-80 z-50 flex items-center justify-center">
          <div className="relative w-full max-w-4xl h-[56.25vw] max-h-[80vh]">
            <iframe
              src={`https://www.youtube.com/embed/${activeCharacter.videoId}?autoplay=1`}
              className="absolute inset-0 w-full h-full"
              frameBorder="0"
              allow="autoplay; encrypted-media"
              allowFullScreen
              title={`${activeCharacter.name} video`}
            />
            <button
              className="absolute top-2 right-2 text-white bg-red-600 px-3 py-1 rounded"
              onClick={() => setActiveCharId(null)}
            >
              âœ• Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AnimefyLayout;
