import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import CharacterCard from '../components/characterCard';
import VideoPlayer from '../components/VideoPlayer';
import change_image_character from '../utils/changeCharaterImage';
import characters from '../data/characterData';

const AnimePage = () => {
  const [activeCharacter, setActiveCharacter] = useState(null);

  const handlePlay = (name) => {
    if (activeCharacter === name) {
      change_image_character(name, 2); // stop
      setActiveCharacter(null); // show all cards again
    } else {
      if (activeCharacter) {
        change_image_character(activeCharacter, 2); // stop previous if any
      }
      change_image_character(name, 1); // play new one
      setActiveCharacter(name); // show only this card
    }
  };

  const getVideoId = (characterName) => {
    const found = characters.find(c => c.name === characterName);
    return found ? found.videoId : '';
  };

  return (
    <div className="text-gray-800">
      <Navbar />
      <div className="container w-full mx-auto px-4 py-6">
        {characters
          .filter(char => !activeCharacter || char.name === activeCharacter) // filter logic
          .map(char => (
            <CharacterCard
              key={char.name}
              character={char}
              onPlay={handlePlay}
              isActive={activeCharacter === char.name}
            />
        ))}
        <VideoPlayer character={activeCharacter} videoId={getVideoId(activeCharacter)} />
      </div>
    </div>
  );
};

export default AnimePage;
