// Import image change function
import change_image_character from '../scripts/changeCharaterImage.js';

// Character data
const characters = [
  {
    id: "Gojo",
    name: "Gojo",
    quote: "Limitless power, infinite style — I'm Gojo Satoru, remember the name.",
    image: "https://i.postimg.cc/7CRyJkw0/gojo.png",
    waveColor: "bg-red-300",
  },
  {
    id: "Naruto",
    name: "Naruto",
    quote: "I’m not gonna run away, I never go back on my word — that’s my ninja way!",
    image: "https://i.postimg.cc/Yvb7kFPX/Naruto.png",
    waveColor: "bg-blue-600"
  },
  {
    id: "Tanjiro",
    name: "Tanjiro",
    quote: "No matter how many times I fall, I’ll stand up again — for the people who can't.",
    image: "https://i.postimg.cc/rdskpmXq/Tanjiro.png",
    waveColor: "bg-pink-600"
  },
  {
    id: "Levi",
    name: "Levi",
    quote: "The only thing we're allowed to do... is to keep moving forward.",
    image: "https://i.postimg.cc/yDVKwjLg/levi.png",
    waveColor: "bg-cyan-500"
  },
  {
    id: "Asta",
    name: "Asta",
    quote: "I don’t need a four-leaf clover — I’ve got hard work and guts!",
    image: "https://i.postimg.cc/Mv0xPNmJ/Asta.png",
    waveColor: "bg-green-500"
  },
  {
    id: "Saitama",
    name: "Saitama",
    quote: "I’m just a guy who’s a hero for fun.",
    image: "https://i.postimg.cc/7frwNw7N/saitama.png",
    waveColor: "bg-purple-500"
  }
];

// Render cards dynamically
const container = document.getElementById("cardContainer");

characters.forEach((char) => {
  const card = document.createElement("div");
  card.className = `card relative text-start ${char.waveColor} p-4 m-4`;
  card.style.borderRadius = "10px";

  card.innerHTML = `
    <svg class="wave" viewBox="0 0 1440 320" preserveAspectRatio="none">
      <path fill="#ffffff" fill-opacity="0.5" d="M0,160 C480,260 960,60 1440,160 L1440,0 L0,0 Z"></path>
    </svg>
    <img id="${char.id}" src="${char.image}" alt="${char.name}"
      class="card_image absolute transition-all duration-500 ease-in-out" />
    <div class="card-body flex justify-between items-center">
      <header>
        <h2 class="card-title transition-all duration-500 ease-in-out">${char.name}</h2>
      </header>
      <br />
      <p class="card-text transition-all duration-1000 ease-in-out">
        "${char.quote}"
      </p>
      <button data-character="${char.id}" data-color="${char.waveColor}"
        class="playbtn duration-1000 ease-in-out text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800">
        play
      </button>
      <div class="video-container hidden mt-4 w-full"></div>
    </div>
  `;

  container.appendChild(card);
});

// Toggle mobile menu
const menuBtn = document.getElementById("menuBtn");
const mobileMenu = document.getElementById("mobileMenu");
menuBtn.addEventListener("click", () => {
  mobileMenu.classList.toggle("hidden");
});
playButtons.forEach((button) => {
  button.addEventListener("click", function () {
    const character = this.getAttribute("data-character");
    const color = this.getAttribute("data-color");
    const card = this.closest(".card");
    const videoPlayer = document.getElementById("videoPlayer");

    const isActive = button.innerText.toLowerCase() === "play";

    if (isActive) {
      document.querySelectorAll(".card").forEach((vc) => {
        if (vc !== card) vc.classList.add("hidden");
      });
      button.innerText = "Hide";
      button.className =
        "playbtn text-white bg-red-700 hover:bg-red-800 focus:ring-4 focus:ring-red-300 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-red-600 dark:hover:bg-red-700 focus:outline-none dark:focus:ring-red-800";
      card.classList.add("Activecard");

      change_image_character(character, 1, color);

      // Optional: Show video player
      videoPlayer.innerHTML = `
        <div class="relative w-full h-0 pb-[56.25%]">
          <iframe
            src="https://www.youtube.com/embed/VIDEO_ID?autoplay=1"
            class="absolute inset-0 w-full h-full"
            frameborder="0"
            allow="autoplay; encrypted-media"
            allowfullscreen
            title="${character} video"
          ></iframe>
        </div>
      `;
      videoPlayer.classList.remove("hidden");
    } else {
      document.querySelectorAll(".card").forEach((vc) => {
        vc.classList.remove("hidden");
      });
      button.innerText = "Play";
      button.className =
        "playbtn text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 me-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800";
      card.classList.remove("Activecard");
      change_image_character(character, 2, color);
      videoPlayer.innerHTML = "";
      videoPlayer.classList.add("hidden");
    }
  });
});
