import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import logo from '../assets/animefyLogo.png';

const Navbar = () => {
  const [mobileMenuVisible, setMobileMenuVisible] = useState(false);

  const linkStyle = ({ isActive }) =>
    `hover:text-pink-300 transition duration-300 ${isActive ? 'text-pink-300 font-semibold' : ''}`;

  return (
    <nav className="bg-gradient-to-r from-purple-900 via-indigo-900 to-blue-900 text-white shadow-md ">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          <div className="flex items-center space-x-2">
            <img src={logo} alt="Animefy Logo" className="h-8" />
            <NavLink to="/" className="text-2xl font-bold text-pink-400">ðŸŒ¸ Animefy</NavLink>
          </div>

          {/* Desktop Menu */}
          <div className="hidden md:flex space-x-6 text-sm font-medium">
            <NavLink to="/" className={linkStyle}>Home</NavLink>
            <NavLink to="/about" className={linkStyle}>About</NavLink>
            <NavLink to="/services" className={linkStyle}>Services</NavLink>
            <NavLink to="/contact" className={linkStyle}>Contact</NavLink>
          </div>

          {/* Mobile Hamburger */}
          <div className="md:hidden">
            <button onClick={() => setMobileMenuVisible(!mobileMenuVisible)}>
              <svg className="h-6 w-6 text-pink-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2"
                      d="M4 6h16M4 12h16M4 18h16"/>
              </svg>
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {mobileMenuVisible && (
        <div className="md:hidden px-4 pb-4 space-y-2 text-sm font-medium">
          <NavLink to="/" className={linkStyle}>Home</NavLink>
          <NavLink to="/about" className={linkStyle}>About</NavLink>
          <NavLink to="/services" className={linkStyle}>Services</NavLink>
          <NavLink to="/contact" className={linkStyle}>Contact</NavLink>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
