import React from 'react';

const CharacterCard = ({ character, isActive, onPlay }) => {
  return (
    <div
      className={`card transition-all duration-300 ease-in-out ${
        isActive ? 'Activecard' : ''
      } ${!isActive && isActive !== null ? 'hidden' : ''}`}
    >
      <div className="p-4 bg-white rounded shadow-md mb-4 text-center">
        <h2 className="text-xl font-bold mb-2">{character.name}</h2>

        {/* Play/Stop Button */}
        <button
          className={`playbtn text-white ${
            isActive
              ? 'bg-red-700 hover:bg-red-800 focus:ring-red-300 dark:bg-red-600 dark:hover:bg-red-700 dark:focus:ring-red-800'
              : 'bg-blue-700 hover:bg-blue-800 focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800'
          } focus:ring-4 font-medium rounded-lg text-sm px-5 py-2.5 mb-4`}
          data-character={character.name}
          onClick={() => onPlay(character.name)}
        >
          {isActive ? 'Stop' : 'Play'}
        </button>

        {/* Video Container */}
        {isActive && (
          <div className="video-container mt-4" id="videoPlayer">
            <iframe
              width="100%"
              height="200"
              src={`https://www.youtube.com/embed/${character.videoId}?autoplay=1`}
              title={character.name}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            ></iframe>
          </div>
        )}
      </div>
    </div>
  );
};

export default CharacterCard;
