import React from 'react';

const CharacterCard = ({ character, onPlay, isActive }) => {
  return (
    <>
       <svg class="wave" viewBox="0 0 1440 320" preserveAspectRatio="none">
      <path fill="#ffffff" fill-opacity="0.5" d="M0,160 C480,260 960,60 1440,160 L1440,0 L0,0 Z"></path>
    </svg>
    <div className={`card p-4 border shadow rounded mb-4 ${isActive ? 'Activecard' : ''}`}>
      <h2 className="text-xl font-bold mb-2">{character.name}</h2>
      <img className='card_image absolute transition-all duration-500 ease-in-out' src={`${character.image}`}></img>
       <div class="card-body flex justify-between items-center">
           <header>
             <h2 class="card-title transition-all duration-500 ease-in-out">${char.name}</h2>
           </header>
           <br />
       </div>
      <button
        className={`playbtn text-white ${isActive ? 'bg-red-700' : 'bg-blue-700'} hover:bg-opacity-80 focus:ring-4 font-medium rounded-lg text-sm px-5 py-2.5`}
        data-character={character.name}
        onClick={() => onPlay(character.name)}
      >
        {isActive ? 'stop' : 'play'}
      </button>
    </div>
    </>
  );
};

export default CharacterCard;
